create_fc_annotation <- function(ranges, id_col = "id"){                                                                  # Creates valid input {anno} to Rsubread::featureCounts(annot.ext = {anno})                                                                                                                                                                     #testthat::expect_named(ranges, c("seqnames", "start", "end", id_col), ignore.order = T)                                                                                                                                                        ranges %>%                                                                                                                dplyr::rename(Chr = seqnames,                                                                                                         Start = start,                                                                                                          End = end,                                                                                                              GeneID = {{id_col}}) %>%                                                                                  dplyr::select(GeneID, Chr, Start, End) %>%                                                                              dplyr::mutate(Strand = "*") %>%                                                                                         data.frame                                                                                                          } 

